INSERT INTO products VALUES (17, 'Фигурка «Бэтмен на гаргулье»', 58, 'Коллекционная фигурка Бэтмена', 2000.00, 1, '2017-02-15');
INSERT INTO products VALUES (18, 'Валериан: Полное собрание. Книга 1', 54, 'Валериан и Лорелин, придуманные талантливыми Пьером Кристеном и Жан-Клодом Мезьером, впервые появились на страницах журнала «Pilote» в 1967 году. Эти оригинальные истории сразу же стали звездами первой величины на небосклоне научной фантастики.\n\n
В книгу включены дополнительные материалы: кадры из фильма, фотографии со съемочной площадки, интервью с режиссером и многое другое. Серия выпущена по образцу оригинальных сборников издательства «Dargaud» – омнибусами по три книги в одном томе увеличенного формата.', 860.00, 3, '2017-07-19');
INSERT INTO products VALUES (19, 'Человек-Паук. Энциклопедия персонажей', 57, NULL, 1650.00, 1, '2017-07-25');
INSERT INTO products VALUES (20, 'Mass Effect. Андромеда. Восстание на "Нексусе"', 56, NULL, 350.00, 1, '2017-07-25');
